#include <iostream>
#include <hiredis/hiredis.h>

int main(int argc, char *argv[]) {
    std::cout << "Start connecting" << std::endl;

    /* Create Redis context and establish connection */
    redisContext* c = redisConnect("test-redis-001.gfg2gn.0001.apse1.cache.amazonaws.com", 6379);
    if (c == NULL || c->err) {
        std::cerr << "Error Redis context and establish connection." << std::endl;
    }
    const char* key = "123";
    if (argc > 1 && argv[1][0] == 'w') {
        const char* data12k = ""
            ""
            ""
            ""
            ""
            ""
            ""
            ""
            ""
            ""
            ""
            "";
    
        redisReply* set_reply = (redisReply*)redisCommand(c,
            "HMSET %s area area Data1 \"%s\" Data2 \"%s\" Data3 \"%s\" Data4 \"%s\" UpdateTime 1234567890 GameID 123 IDC 123 Server 123.45.67.89"
            , key, data12k, data12k, data12k, data12k);
        if (set_reply == NULL || set_reply->type == REDIS_REPLY_ERROR) {
            std::cerr << "SET command failed: " << c->errstr << std::endl;
            freeReplyObject(set_reply);
            redisFree(c);
            exit(1);
        }
        freeReplyObject(set_reply);
    
        redisReply* expire_reply = (redisReply*)redisCommand(c, "EXPIRE %s 3600", key);
        if (expire_reply == NULL || expire_reply->type == REDIS_REPLY_ERROR) {
            std::cerr << "EXPIRE command failed: " << c->errstr << std::endl;
            freeReplyObject(expire_reply);
            redisFree(c);
            exit(1);
        }
        freeReplyObject(set_reply);
    
        std::cout << "SET operation successful" << std::endl;
    } else {
        redisReply* range_reply = (redisReply*)redisCommand(c, "ZRANGEBYSCORE %s -inf 999999999 WITHSCORES LIMIT 0 20", key);
        if (range_reply == NULL || range_reply->type != REDIS_REPLY_ARRAY) {
            std::cerr << "ZRANGEBYSCORE command failed: " << c->errstr << std::endl;
            freeReplyObject(range_reply);
            redisFree(c);
            exit(1);
        }
        for (int j = 0; j < range_reply->elements; j++) {
            redisReply* rem_reply = (redisReply*)redisCommand(c, "ZREM %s %s", j, key, range_reply->element[j]->str);
            if (rem_reply == NULL || rem_reply->type == REDIS_REPLY_ERROR) {
                std::cerr << "ZREM command failed: " << c->errstr << std::endl;
                freeReplyObject(rem_reply);
                redisFree(c);
                exit(1);
            }
            freeReplyObject(rem_reply);
        }
        //std::cout << "GET operation successful. Value: " << get_reply->str << std::endl;
    }
    redisFree(c);
    std::cout << "connect end" << std::endl;

    return 0;
}
